prompt.interfaceCode:
export interface Prompt {
  id: string;
  name: string;
  description: string;
  content: string;
  category: string;
  tags: string[];
  createdAt: string;
  updatedAt: string;
}

storageprovider.interfaceCode:
export interface StorageProvider {
  name: string;
  type: 'local' | 'onedrive' | 'googledrive' | 'dropbox' | 'icloud';

  // Verifica se o provider está disponível no sistema
  isAvailable(): Promise<boolean>;

  // Obtém caminho sugerido padrão
  getDefaultPath(): string;

  // Valida se o caminho configurado é válido e gravável
  validatePath(path: string): Promise<boolean>;

  // Carrega prompts do storage
  load(path: string): Promise<Prompt[]>;

  // Salva prompts no storage
  save(path: string, prompts: Prompt[]): Promise<void>;

  // Verifica se arquivo existe
  exists(path: string): Promise<boolean>;
}

storageconfig.interfaceCode:
export interface StorageConfig {
  provider: 'local' | 'onedrive' | 'googledrive' | 'dropbox' | 'icloud';
  path: string;
  autoSync: boolean;
  fallbackToLocal: boolean;
  lastSyncAt?: string;
  metadata?: {
    deviceName?: string;
    cloudAccountId?: string;
  };
}
